package com.projects.api;import com.projects.data.DaoFactory;import com.projects.data.dto.BankProcessor;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import javax.inject.Inject;import java.util.List;/** * Created by Adeola.Ojo */@Controller@RequestMapping("/autopay")public class LookupController {    @Inject    DaoFactory factory;    @RequestMapping("/landingpage")    public ModelAndView landingPage(){        ModelAndView mav = new ModelAndView("bank_information");        mav.addObject("bankProcessors", bankProcessors());        return mav;    }    @ModelAttribute("bankProcessors")    public List<BankProcessor> bankProcessors(){        return factory.autoPayBankProcessorDao.fetchAll();    }}