package com.projects.config;import org.springframework.context.annotation.Bean;import javax.inject.Inject;import javax.sql.DataSource;import org.springframework.context.annotation.Configuration;import org.springframework.core.env.Environment;import org.springframework.jdbc.datasource.DriverManagerDataSource;/** * Created by Adeola.Ojo */@Configurationpublic class DatabasePropertyConfig {    @Inject    Environment environment;    @Bean(name = "localDataSource")    public DataSource localDataSource() {        final DriverManagerDataSource configDataSource = new DriverManagerDataSource();        configDataSource.setDriverClassName(environment.getProperty("autopay.database.classname"));        configDataSource.setUrl(environment.getProperty("autopay.database.url"));        configDataSource.setUsername(environment.getProperty("autopay.database.username"));        configDataSource.setPassword(environment.getProperty("autopay.database.password"));        return configDataSource;    }}