package com.projects.config;import com.projects.util.intercept.LoggingRequestInterceptor;import org.springframework.context.annotation.Bean;import org.springframework.core.env.Environment;import org.springframework.http.client.ClientHttpRequestInterceptor;import org.springframework.stereotype.Component;import org.springframework.web.client.DefaultResponseErrorHandler;import org.springframework.web.client.RestTemplate;import javax.inject.Inject;import java.util.ArrayList;import java.util.List;/** * Created by Adeola.Ojo */@Componentpublic class RestTemplateConfig {    @Inject    Environment environment;    @Bean    public RestTemplate pingPongTemplate() {        RestTemplate template = new RestTemplate();        List<ClientHttpRequestInterceptor> interceptors = new ArrayList<ClientHttpRequestInterceptor>();        template.setInterceptors(interceptors);        template.setErrorHandler(new DefaultResponseErrorHandler() {        });        return template;    }}