package com.projects.util.intercept;import com.projects.App;import io.vertx.core.json.JsonObject;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.http.HttpRequest;import org.springframework.http.client.ClientHttpRequestExecution;import org.springframework.http.client.ClientHttpRequestInterceptor;import org.springframework.http.client.ClientHttpResponse;import java.io.IOException;/** * Created by Adeola.Ojo */public class LoggingRequestInterceptor implements ClientHttpRequestInterceptor {    private static final Logger logger = LoggerFactory.getLogger(App.class);    @Override    public ClientHttpResponse intercept(HttpRequest httpRequest, byte[] bytes, ClientHttpRequestExecution clientHttpRequestExecution) throws IOException {        try {            String bodyString = new String(bytes, "UTF-8");            if (bytes != null && bytes.length > 0) {                JsonObject payloadJson = new JsonObject(bodyString);            }            ClientHttpResponse response = clientHttpRequestExecution.execute(httpRequest, bytes);            return response;        } catch (Exception ex) {            return null;        }    }}