package com.projects.util.security;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;/** * Created by Adeola.Ojo */public class Hash {    public static String getSha512(String dataString) throws NoSuchAlgorithmException {        MessageDigest md = MessageDigest.getInstance("SHA-512");        byte[] shaData = md.digest(dataString.getBytes());        return getHexString(shaData);    }    public static String getMD5Hash(String dataString) throws NoSuchAlgorithmException {        MessageDigest md = MessageDigest.getInstance("MD5");        byte[] shaData = md.digest(dataString.getBytes());        return getHexString(shaData);    }    private static String getHexString(byte[] b) {        String result = "";        for (int i = 0; i < b.length; i++) {            result += Integer.toString((b[i] & 0xff) + 0x100, 16).substring(1);        }        return result;    }}